
public class DTORestaurant {
	int p_no;
    String p_location;
    String p_owner;
    int p_seat;
    
    public DTORestaurant() {
		// TODO Auto-generated constructor stub
	}

	public int getP_no() {
		return p_no;
	}

	public void setP_no(int p_no) {
		this.p_no = p_no;
	}

	public String getP_location() {
		return p_location;
	}

	public void setP_location(String p_location) {
		this.p_location = p_location;
	}

	public String getP_owner() {
		return p_owner;
	}

	public void setP_owner(String p_owner) {
		this.p_owner = p_owner;
	}

	public int getP_seat() {
		return p_seat;
	}

	public void setP_seat(int p_seat) {
		this.p_seat = p_seat;
	}
    
    
}
